(ns advent-2015.day-1
  (:require
    [advent.util :as util]))

(defn part-1 [input]
  (util/sum (map {\( 1
                  \) -1}
                 input)))

(defn part-2 [input]
  (first (keep-indexed (fn [i sum]
                         (when (= sum -1)
                           i))
                       (reductions +
                                   0
                                   (map {\( 1
                                         \) -1}
                                        input)))))


