(ns advent-2020.day-5
  (:require [clojure.string :as str]))

(defn parse [in]
  (into []
        (map (fn [l]
               (let [r (subs l 0 7)
                     c (subs l 7)]
                 {:raw l
                  :raw-row r
                  :raw-col c
                  :row-int (Long/parseLong (-> r
                                               (str/replace \F \0)
                                               (str/replace \B \1))
                                           2)
                  :col-int (Long/parseLong (-> c
                                               (str/replace \L \0)
                                               (str/replace \R \1))
                                           2)})))
        (str/split-lines in)))

(defn seat-id [{r :row-int
                c :col-int}]
  (+ (* r 8)
     c))


(defn part-1 [in]
  (apply max
         (map seat-id
              (parse in))))


(defn part-2 [in]
  (some (fn [[l r]]
          (when (= r (+ 2 l))
            (inc l)))
        (partition-all 2
                       1
                       (sort (map seat-id
                                  (parse in))))))

