(ns advent-2020.day-7
  (:require [clojure.string :as str]
            [ubergraph.core :as uber]
            [ubergraph.alg :as ualg]
            [loom.alg :as lalg]
            [clojure.set :as set]))

(defn parse [in]
  (uber/add-directed-edges* (uber/graph)
                            (mapcat (fn [l]
                                      (let [[_ p c] (re-find #"([a-z]+ [a-z]+) bags contain (.*)"
                                                             l)]
                                        (keep (fn [[_ g1 n t]]
                                                (when (not= g1 "no other")
                                                  [p t {:count (Long/parseLong n)}]))
                                              (re-seq #"((\d+) ([a-z]+ [a-z]+)|no other) bags?"
                                                      c))))
                                    (str/split-lines in))))


(defn paths-to [g d]
  (keep (fn [n]
          (when-not (= n d)
            (ualg/shortest-path g n d)))
        (uber/nodes g)))


(defn part-1 [in]
  (count (paths-to (parse in)
                   "shiny gold")))


(defn part-2 [in]
  (ualg/shortest-path (parse in)
                      "shiny gold"
                      :count))

(comment
