          "1 bright white bag, 2 muted yellow bags."
          )

  (part-2 "light red bags contain 1 bright white bag, 2 muted yellow bags.\ndark orange bags contain 3 bright white bags, 4 muted yellow bags.\nbright white bags contain 1 shiny gold bag.\nmuted yellow bags contain 2 shiny gold bags, 9 faded blue bags.\nshiny gold bags contain 1 dark olive bag, 2 vibrant plum bags.\ndark olive bags contain 3 faded blue bags, 4 dotted black bags.\nvibrant plum bags contain 5 faded blue bags, 6 dotted black bags.\nfaded blue bags contain no other bags.\ndotted black bags contain no other bags.")
  (def g (parse "light red bags contain 1 bright white bag, 2 muted yellow bags.\ndark orange bags contain 3 bright white bags, 4 muted yellow bags.\nbright white bags contain 1 shiny gold bag.\nmuted yellow bags contain 2 shiny gold bags, 9 faded blue bags.\nshiny gold bags contain 1 dark olive bag, 2 vibrant plum bags.\ndark olive bags contain 3 faded blue bags, 4 dotted black bags.\nvibrant plum bags contain 5 faded blue bags, 6 dotted black bags.\nfaded blue bags contain no other bags.\ndotted black bags contain no other bags."))
  g
  (filter (complement :end)
          )
  (ualg/pre-traverse g)

  tree-seq
  (map ualg/pprint-path
       (ualg/shortest-path g
                           {:start-node "shiny gold"
                            :traverse true}))

  (ualg/pre-traverse g "shiny gold")

  (map ualg/cost-of-path
       (ualg/shortest-path g
                           {:start-node "shiny gold"
                            :cost-attr :count
                            :traverse true}))
  (reduce +
          0
          )
  (uber/find-edges g
                   {:dest "shiny gold"})

  (uber/pprint g)
  (paths-to g "shiny gold")
  (ualg/shortest-path g "shiny gold" "shiny gold")

  (lalg/post-traverse g)
  (ualg/shortest-path g {:dest "shiny gold"})
  (ualg/all-destinations "shiny gold")

