(ns advent-2021.day-3
  (:require
    [advent.util :as util]
    [clojure.string :as str]))


(defn parse [in]
  (mapv (fn [l]
          (mapv #(Character/digit ^Character %
                                  10)
                l))
        (str/split-lines in)))


(defn bits->long [bits]
  (Long/valueOf (str/join bits)
                2))


(defn common-bit
  [comp bits]
  (let [{c0 0
         c1 1} (frequencies bits)]
    (if (comp c1 c0)
      1
      0)))


(defn gamma-rate [dat]
  (bits->long (into []
                    (map (partial common-bit >))
                    (util/transpose dat))))


(defn flip-bits
  "Flip all bits starting from most significant bit n."
  [x n]
  (bit-xor x
           (- (long (Math/pow 2 n))
              1)))


(defn part-1 [dat]
  (let [gr (gamma-rate dat)
        er (flip-bits gr
                      (count (first dat)))]
    (* gr er)))


(defn find-num [nums comp]
  (reduce (fn [nums n]
            (if (= 1 (count nums))
              (reduced (bits->long (first nums)))
              (let [cb (common-bit comp
                                   (get (util/transpose nums)
                                        n))]
                (into []
                      (filter (fn [num]
                                (= cb (get num n))))
                      nums))))
          nums
          (range (count (first nums)))))


(defn part-2 [dat]
  (let [o2-gen (find-num dat >=)
        co2-scrub (find-num dat <)]
    (* o2-gen co2-scrub)))


